import UseInput from "../Hooks/use-input";
import "./Form.css";
const nameValidation = (value) => value.trim() !== "";

const BasicForm = (props) => {
  const {
    value: enteredName,
    isValid: nameIsValid,
    hasError: nameInputHassError,
    inputBlurHandler: nameBlurHandler,
    valueChangeHandler: nameChangeHandler,
    reset: nameReset,
  } = UseInput(nameValidation);

  const {
    value: enteredLastName,
    isValid: lastNameIsValid,
    hasError: lastNameInputHassError,
    inputBlurHandler: lastNameBlurHandler,
    valueChangeHandler: lastNameChangeHandler,
    reset: lastNameReset,
  } = UseInput(nameValidation);

  const {
    value: enteredEmail,
    isValid: emailIsValid,
    hasError: emailInputHassError,
    inputBlurHandler: emailBlurHandler,
    valueChangeHandler: emailChangeHandler,
    reset: emailReset,
  } = UseInput((value) => {
    return value.trim() !== "" && value.trim().includes("@");
  });
  const {
    value: enteredAdress,
    isValid: adressIsValid,
    hasError: adressInputHassError,
    inputBlurHandler: adressBlurHandler,
    valueChangeHandler: adressChangeHandler,
    reset: adressReset,
  } = UseInput((value) => {
    return value.trim() !== "" && /\d/.test(value);
  });

  const userInformation = [];

  const backEnd = async () => {
    const back = await fetch(
      "https://realistic-example-483a6-default-rtdb.firebaseio.com/user.json",
      {
        method: "POST",
        body: JSON.stringify(userInformation),
      }
    );
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();

    if (!nameIsValid || !emailIsValid || !lastNameIsValid) {
      return;
    }

    console.log(enteredName, enteredLastName, enteredEmail);

    userInformation.push({
      name: enteredName,
      lastName: enteredLastName,
      email: enteredEmail,
    });
    backEnd();
    nameReset();
    lastNameReset();
    emailReset();
  };

  let formIsValid;

  if (nameIsValid && emailIsValid && lastNameIsValid) {
    formIsValid = true;
  }

  const nameInputClass = nameInputHassError
    ? "form-control invalid"
    : "form-control ";

  const emailInputClass = emailInputHassError
    ? "form-control invalid"
    : "form-control ";

  const lastNameInputClass = lastNameInputHassError
    ? "form-control invalid"
    : "form-control ";

  const adressInputClass = adressInputHassError
    ? "form-control invalid"
    : "form-control ";

  return (
    <form className="form" onSubmit={onSubmitHandler}>
      <div className="control-group">
        <div className={nameInputClass}>
          <label htmlFor="name">First Name</label>
          <input
            onChange={nameChangeHandler}
            onBlur={nameBlurHandler}
            value={enteredName}
            type="text"
            id="name"
          />
          {nameInputHassError && (
            <p className="error-text">Name input must not be empty</p>
          )}
        </div>
        <div className={lastNameInputClass}>
          <label htmlFor="name">Last Name</label>
          <input
            onChange={lastNameChangeHandler}
            onBlur={lastNameBlurHandler}
            value={enteredLastName}
            type="text"
            id="name"
          />
          {lastNameInputHassError && (
            <p className="error-text">Last Name input must not be empty</p>
          )}
        </div>

        <div className={emailInputClass}>
          <label htmlFor="name">E-Mail Address</label>
          <input
            onChange={emailChangeHandler}
            onBlur={emailBlurHandler}
            value={enteredEmail}
            type="text"
            id="name"
          />
          {emailInputHassError && <p className="error-text">E-mail invalid.</p>}
        </div>
        <div className={adressInputClass}>
          <label htmlFor="name">Adress</label>
          <input
            onChange={emailChangeHandler}
            onBlur={emailBlurHandler}
            value={enteredEmail}
            type="text"
            id="name"
          />
          {emailInputHassError && <p className="error-text">E-mail invalid.</p>}
        </div>
        <div className="form-actions">
          <button disabled={!formIsValid}>Submit</button>
        </div>
      </div>
    </form>
  );
};

export default BasicForm;
