import { useReducer } from "react";
import CartContext from "./cart-context";

let defaultCartState = { items: [], totalAmount: 0 };
console.log(defaultCartState, " CARRR STTEEEE");

const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    const updatedTotalAmount =
      state.totalAmount + action.items.price * action.items.amount;

    const existingCartItemIndex = state.items.findIndex(
      (item) => item.id === action.items.id
    );

    const existingCartItem = state.items[existingCartItemIndex];

    let updateItems;

    if (existingCartItem) {
      const updateItem = {
        ...existingCartItem,
        amount: existingCartItem.amount + action.items.amount,
      };
      updateItems = [...state.items];
      updateItems[existingCartItemIndex] = updateItem;

      console.log(
        "State",
        state,
        "Action",
        action,
        "CarItemIndex",
        existingCartItemIndex,
        "ExistingCartItem",
        existingCartItem,
        "UpdatedItem",
        updateItem,
        "UpdatedItemss",
        updateItems
      );
    } else {
      updateItems = state.items.concat(action.items);
    }

    return (defaultCartState = {
      items: updateItems,
      totalAmount: updatedTotalAmount,
    });
  }

  if (action.type === "REMOVE") {
    const existingCartItemIndex = state.items.findIndex(
      (item) => item.id === action.id
    );
    const existingItem = state.items[existingCartItemIndex];

    const updatedTotalAmount = state.totalAmount - existingItem.price;

    let updatedItems;
    if (existingItem.amount === 1) {
      updatedItems = state.items.filter((item) => item.id !== action.id);
    } else {
      const updatedItem = {
        ...existingItem,
        amount: existingItem.amount - 1,
      };
      updatedItems = [...state.items];
      updatedItems[existingCartItemIndex] = updatedItem;
    }

    return (defaultCartState = {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    });
  }
  return defaultCartState;
};

const CartProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );

  const addItemToCartHandler = (item) => {
    dispatchCartAction({ type: "ADD", items: item });
  };

  const removeItemToCartHandler = (id) => {
    dispatchCartAction({ type: "REMOVE", id: id });
  };

  const cartContext = {
    items: defaultCartState.items,
    totalAmount: defaultCartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemToCartHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};
export default CartProvider;
